import React,{useState} from "react";
export default function FormData() {
  let [text, setText]=useState("");
  let [copy, setCopy]=useState(text);
  let upper=()=>{
    setCopy(text);
    setCopy(copy.toUpperCase());
  }
  let handleOnChange=(event)=>{
    setText(event.target.value);
    setCopy(event.target.value);
  }
  let lower=()=>{
    setCopy(text.toLowerCase());
  }
  let numOfWords=()=>{
    setCopy(text.split(" ").length);
  }
  let numOfCharacters=()=>{
    setCopy(text.length);
  }
  let timeRead=()=>{
    setCopy(text.split(" ").length*0.0042*60+"seconds");
  }
  return (
    <div className="container">
      <div className="mb-3 my-5">
        <label htmlFor="exampleFormControlTextarea1" className="form-label">
          Enter your text to analyse
        </label>
        <textarea
          className="form-control"
          id="exampleFormControlTextarea1"
          rows="3" value={copy} onChange={handleOnChange}
        ></textarea>
        <button className="btn btn-primary mx-1 my-3" onClick={upper}>UpperCase</button>
        <button className="btn btn-primary mx-1 my-3" onClick={lower}>LowerCase</button>
        <button className="btn btn-primary mx-1 my-3" onClick={numOfWords}>Word Count</button>
        <button className="btn btn-primary mx-1 my-3" onClick={numOfCharacters}>Character count</button>
        <button className="btn btn-primary mx-1 my-3" onClick={timeRead}>Reading Time</button>
      </div>
    </div>
  );
}
